mapper.select:
select 
from base_operation_log t



            and t.business_id = #{condition.searchMap.businessId}


            and t.business_type = #{condition.searchMap.businessType}


            and t.operation_type = #{condition.searchMap.operationType}



            and t.create_time >= #{condition.searchMap.createTimeList[0]}
            and t.create_time <= #{condition.searchMap.createTimeList[1]}



            and t.create_user like CONCAT('%', #{condition.searchMap.createUser}, '%')



            and t.last_modified >= #{condition.searchMap.modifyTimeList[0]}
            and t.last_modified <= #{condition.searchMap.modifyTimeList[1]}



            and t.last_modify_user like CONCAT('%', #{condition.searchMap.lastModifyUser}, '%')



order by business_type,operation_type, last_modified desc

mapper.insert:
insert into base_operation_log(
    business_id
    , business_type
    , operation_type
    , comments
    , last_modify_user
    , last_modified
    , create_user
    , create_time
 ) values ( #{businessId}
          , #{businessType}
          , #{operationType}
          , #{comments}
          , #{lastModifyUser}
          , #{lastModified}
          , #{createUser}
          , #{createTime}
 )
